class Register extends React.Component {

    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        // initial states
        this.state = {
            loading: false,
            email: "",
            password: "",
            // workshopsByCategory: null,
            // email: "",
            // password: "",
        }

        this.on_register = this.on_register.bind(this);

    }

    on_register() {

        const email = this.state.email.trim();
        const password = this.state.password;

        auth.createUserWithEmailAndPassword(email, password)

            .then(res => {

                // create new user in database
                const newUser = {



                };

                alert("Registered successfully!");

                this.props.history.push('/home');

            })
            .catch(e => {
                alert(e);
            })
    }

    componentDidMount() {

        // // check if user is logged
        // auth.onAuthStateChanged((user) => {

        //     if (user) {

        //         // console.log("user logged");

        //         this.props.history.push('/home');

        //     }

        //     else {

        //         // console.log("user no logged");

        //         // this.props.history.push('/login/');
        //     }

        // });


    }

    render() {

        // return method
        return (

            <Container
                style={{
                    // margin: 20,
                    // padding: 20,
                    // backgroundColor: "red",
                }}
            >

                {
                    !this.state.loading

                        ?

                        <Container>

                            {/* title */}
                            <Paper
                                style={{
                                    margin: 15,
                                    padding: 5,
                                    display: "flex",
                                    flexDirection: "column",
                                }}

                                elevation={5}
                            >

                                <Typography variant="h6" component="h6">

                                    Register to Close

                                </Typography>

                                <Typography variant="body2" component="p">

                                    In here you can match with people you know! It is not a marketplace of people, it is just a way to match with people you know previously!

                                </Typography>

                                <TextField
                                    id="standard-uncontrolled"
                                    label = "Name"
                                    margin="normal"
                                    onChange={(e) => this.setState({ name: e.target.value })}
                                    value={this.state.name}
                                />

                                <TextField
                                    id="standard-uncontrolled"
                                    label="Link to profile"
                                    margin="normal"
                                    helperText = "It can be your Instagram or Whatsapp link, because people will connect with you using this link! It is really important to add a link where you usually speak"
                                    onChange={(e) => this.setState({ linkToProfile: e.target.value })}
                                    value={this.state.linkToProfile}
                                />

                                <TextField
                                    id="standard-uncontrolled"
                                    label="Email"
                                    margin="normal"
                                    onChange={(e) => this.setState({ email: e.target.value })}
                                    value={this.state.email}
                                />

                                <TextField
                                    id="standard-uncontrolled"
                                    label="Password in this platform"
                                    type="password"
                                    // defaultValue="Correo electrónico"
                                    margin="normal"
                                    onChange={(e) => this.setState({ password: e.target.value })}
                                    value={this.state.password}
                                />


                                {/* register */}
                                <Button align="center" variant="contained" color="secondary" onClick={this.on_register}>
                                    Register
                                </Button>

                            </Paper>

                            {/* box to message of register
                            <Paper
                                style = {{
                                    display: "flex",
                                    flexDirection: "column"
                                }}
