import React from "react";

// import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// material ui
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import { Button, Box } from "@material-ui/core";
import Typography from '@material-ui/core/Typography';
import CircularProgress from "@material-ui/core/CircularProgress";
// import FavoriteBorder from '@material-ui/icons/FavoriteBorder';
// import LiveTv from '@material-ui/icons/LiveTv';

import {
    fs,
    auth,
} from "../../libraries/firebase/firebase";

// //  component
// import WorkshopComponent from "./components/workshop.component";
// import OverviewWorkshop from "./components/overviewWorkshop.component";

// firebase
// import { auth } from "../../config/firebase";
// import { fs } from "../../config/firebase";


class PublicProfile extends React.Component {

    // constructor
    constructor(props) {

        // constructur of parent
        super(props);

        // initial states
        this.state = {
            loading: true,
            // workshopsByCategory: null,
            // garbagesPerDay: [],
            // email: "",
            // password: "",
        }

        this.likeToUser = this.likeToUser.bind(this);

    }

    componentDidMount() {

        // temp
        this.setState({
            loading: false,
            // matchesWithUsers: matchesWithUsers,
        });

        // // check if user is logged
        // auth.onAuthStateChanged((user) => {

        //     if (!user) {

        //         // console.log("user not logged");

        //         this.props.history.push('/');

        //         // console.log("aosjid");

        //     }

        //     else {

        //         // console.log("user logged");
        //         // alert("user logged");

        //         // this.props.history.push('/login/');

        //         // this.setState({
        //         //     userId: user.uid,
        //         // });

        //         // get number of garbage
        //         // fs.collection('oneGarbagePerDay').doc(this.state.userId).collection("oneGarbagePerDay").
        //         //     get().then(snapshotquery => {

        //         //         // // get data from API
        //         //         var garbages = [];

        //         //         // iterate over each item
        //         //         snapshotquery.forEach(doc => {

        //         //             // console.log(doc.data());
        //         //             let garbage = doc.data();
        //         //             // store location
        //         //             // loteo["location"] = [loteo.location.latitude, loteo.location.longitude];
        //         //             // garbage["id"] = doc.id;
        //         //             // add loteo to list
        //         //             garbages.push(garbage);

        //         //         });

        //         //         // update state
        //         //         this.setState({

        //         //             // flag of getting data from API
        //         //             get_garbages: true,
        //         //             // update garbages
        //         //             garbagesPerDay: garbages,

        //         //             loading: false,

        //         //         },

        //         //             // () => {
        //         //             //     console.log(this.state.garbages)
        //         //             // }
        //         //         );

        //         //     });


        //     }

        // });

    }

    likeToUser() {

        // check if user is logged
        auth.onAuthStateChanged((user) => {

            if (!user) {

                console.log("user not logged");

                alert("You need an account in our platform in order to 'give likes'");

                // this.props.history.push('/');

                // console.log("aosjid");

            }

            else {

                const loggedUserRef = fs.collection("users").doc(user.uid);
                const likeUserRef = fs.collection("users").doc(this.props.match.params.userId);

                // get logged user
                loggedUserRef.get()
                .then(doc_ => {

                    var loggedUser = doc_.data();
                    loggedUser["id"] = doc_.id;

                    // check if like already exists
                    // if it does not exists
                    if (!loggedUser.likesToUsers.includes(this.props.match.params.userId)) {

                        // add like to like user
                        loggedUser.likesToUsers.push(this.props.match.params.userId);

                        // get user to give like
                        likeUserRef.get()
                        .then(doc => {

                            var likeUser = doc.data();
                            likeUser["id"] = doc.id;

                            // console.log("logged: ", doc_.id);
                            // console.log("like: ", doc.id);

                            // // test match
                            // likeUser.likesToUsers.push(doc_.id);
                            // loggedUser.likesToUsers.push(doc.id);

                            // // console.log(likeUser.likesToUsers);
                            // console.log("logged: ", loggedUser);
                            // console.log("like: ", likeUser);

                            // update likes of like user
                            if (!likeUser.likesFromUsers.includes(loggedUser.id)) {
                                likeUser.likesFromUsers.push(loggedUser.id);
                            };

                            // check if match already exists
                            if (likeUser.matches.includes(loggedUser.id) & loggedUser.matches.includes(likeUser.id)) {

                                alert("You already did match! Check your profile in order to start to speak!");

                            }

                            // if match does not exist yet
                            else {

                                // check if users are both with likes (match)
                                if (likeUser.likesToUsers.includes(loggedUser.id) & loggedUser.likesToUsers.includes(likeUser.id)) {
                                    // console.log("match");

                                    // create match
                                    likeUser.matches.push(loggedUser.id);
                                    loggedUser.matches.push(likeUser.id);

                                    // user message
                                    alert("Congratulations! You did match! Go to your profile and start talking!");

                                }

                                // do not match yet
                                else {

                                    // usre message
                                    alert("Your like was saved, now you have to wait if the other person gives you like too! Good luck!");
                                    // console.log("other person don't like you");

                                };


                            };

                            // update users on BD
                            loggedUserRef.update(loggedUser).then(res => {
                                console.log("");
                            })

                        });

                    }

                    // if like already exists
                    else{
                        alert("You already liked this person. Must to wait until other person give you like!");
                    };

                })

            }

        });


    }

    render() {

        // return method
        return (

            <Container
                style={{
                    // margin: 20,
                    // padding: 20,
                    // backgroundColor: "red",
                }}
            >

                {
                    !this.state.loading

                        ?

                        <Container>

                            {/* title */}
                            <Paper
                                style={{
                                    margin: 15,
                                    padding: 5,
                                }}

                                elevation={5}
                            >
                                <Typography variant="h6" component="h6" style={{ textAlign: "center", }}>

                                    Hi!

                                </Typography>

                                <Typography variant="p" component="body2" style={{ textAlign: "center", }}>

                                    You can give me "like me" if I like you :) If I gave you like too, so we will do match :) and we will be notified, otherwise I will never know you gave me like :), so don't worry about it!

                                </Typography>

                                {/* like button */}
                                <Button 
                                    variant="contained" 
                                    color="primary"
                                    onClick={() => {
                                        // alert("If I give you like too, we will do match, otherwise I will never know you gave me like :)")
                                        this.likeToUser();
                                    }}
                                >
                                    Like me
                                </Button>

                                <Typography variant="p" component="body2" style={{ textAlign: "center", }}>

                                    If you don't have an account yet, register here

                                </Typography>

                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={() => {
                                        // alert("Go to register");
                                        // this.s
                                        this.props.history.push('/register');
                                    }}
                                >
                                    Register
                                </Button>

                            </Paper>

                        </Container>

                        :

                        <CircularProgress />

                }

            </Container>
        );

    }

}

export default PublicProfile;
